package clase1;

public class ejercicio_1b {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numeroInicio = 5;
		int numeroFin = 14;
		
		while (numeroInicio <= numeroFin) {
			if (numeroInicio % 2 == 0) {
				System.out.print(numeroInicio+", ");
			}
			
			numeroInicio++;
		}

	}

}
